import React,{Component} from "react"
import InstaService from"../services/instaService";
import User from "./User";
import  ErrorMassage from "./Error";
import Loading from './Loading';


export default class Userss extends Component{
    InstaService = new InstaService();
    state = {  
        posts: [],
        error: false,
        loading:true
    }
    componentDidMount() {
        this.updateUser();
    }
    updateUser(){
        this.InstaService.getAllUser()
        .then(this.onPostsLoaded)
        .catch(this.onError);
    }

    onPostsLoaded = (posts) =>{
        this.setState({
            posts,
            error:false,
            loading:false
        });
    }
    onError = (err) =>{
        this.setState({
            error: true
        })
    }


    renderItem(arr){
        return arr.map(item =>{
            const {name,altname,photo,src,alt,descr,id,likes} = item;
            return (
            <div key={id} className="post">
                <Userss src={photo}
                alt={altname}       
                name={name}
                min/>
            </div>
            )
        });
    }

    render() {
        const {error,posts,loading } = this.state;

        if(error){
            return <ErrorMassage posts/>
        }
        if(loading){
            return <Loading/>
        }

        const items = this.renderItems(posts);
        
        return(
            <div className="left">
                {items}
            </div>
        )
    }
}





