    //         {   
    //             id:Date.now(),
    //             likes:"16",
    //             descr: "Hello! If you want to drink some delicious smoothies - just call me!",
    //             src:"https://nighthelper.com/wp-content/uploads/2016/03/nature_waterfall_summer_lake_trees_90400_3840x2160.jpg",

    //             }
    //             // stateChat.map(chat => {
    //             //     if (chat.id === 2) {
    //             //         chat.name = "axali"
    //             //     }return chat 
    //             // })
    //         ])
    // }
return(
    <div>
        {props.statepost.map(item => 
            <div key={item.id} className="post">
            {/* <User src="https://i2.wp.com/www.intelligentliving.co/wp-content/uploads/2019/09/Keanu-Reeves-1.jpg?fit=2100%2C1200&ssl=1"
            alt="picture"      
            name={item.name}
            min/> */}
            <Keanu/>
            <img src={item.src} alt="alt"></img>
            <div className="icon_div">
                <img className="size_icon" src="https://icons-for-free.com/iconfiles/png/512/heart-131965017458786724.png" alt="hear_icon">
                </img>
                <img className="size_icon" src="https://static.thenounproject.com/png/153178-200.png" alt="mess_icon">
                </img>
                <img className="size_icon" src="https://www.clipartkey.com/mpngs/m/21-219017_instagram-paper-airplane-icon.png" alt="air_icon">
                </img>
            </div>
            <div className="likes"> {item.likes} likes</div>
            <div className="post__descr">
                {item.descr}
            </div>
        </div>
        )}
    </div>
    )
}

export default Post;
