import React,{Component} from "react"
import InstaService from "../services/instaService";
// const User = (props) => {
//     const {min, src, alt, name } = props;
//     return(
//         <a href="#" 
//         className={min ? "user min": "user"}>
//             <img src={src} alt={alt}></img>
//             <div>{name}</div>
//         </a>
//     )
// }




export default class User extends Component{

    InstaService = new InstaService();
    state = {  
        posts: [],
        error: false,
        loading:true
    }
    componentDidMount() {
        this.updateUsers();
    }
    updateUsers(){
        this.InstaService.getAllUsers()
        .then(this.onPostsLoaded)
        .catch(this.onError);
    }

    onPostsLoaded = (posts) =>{
        this.setState({
            posts,
            error:false,
            loading:false
        });
        

    }

    onError = (err) =>{
        this.setState({
            error: true
        })
    }

    renderItems(arr){
        return arr.map(item =>{
            const {name,src,alt,id} =item;
            return (
            <div key={id} className="post">
                <a href="#" 
                    className="min">
                    <img src={src} alt={alt}></img>
                    <div>{name}</div>
                </a>
            </div>
            )
        });
    }

    render() {
        const {error,posts,loading } = this.state;

        if(error){
            return <ErrorMassage posts/>
        }
        if(loading){
            return <Loading/>
        }

        const items = this.renderItems(posts);
        
        return(
            <div className="left">
                {items}
            </div>
        )
    }

}
