    componentDidMount(){
        this.updateUsers();
        
    }

    updateUsers (){
        this.InstaService.getAllusers()
            .then(this.onPhotosLoaded)
            .catch(this.onError)
    }

    onError = () =>{
        this.setState({
            error: true
        })
    }

    onPhotosLoaded = (photos) =>{
        this.setState({
            error: false,
            photos //photos:photos//
            // loading :false
        })
    }
    renderItems(arr){
        return arr.map(item => {
            const {src,alt,id} = item;
            return(
                <img key={id} src={src} alt={alt}></img>
                
            )
        });
    }
    
    render(){
        const {error,photos,loading} = this.state;

        if(error){
            return <ErrorMasage/>
